fn main() {
    let input = "";

    let mut i = 0;
    while i < input.chars().count() {
        let group = &input[i..i + 4];
        let v: Vec<char> = group.chars().collect();
        let mut y = v.clone();

        y.sort();
        y.dedup();

        if v.len() == y.len() {
            println!("{}", i + 4);
            break;
        }

        i += 1;
    }

    i = 0;
    while i < input.chars().count() {
        let group = &input[i..i + 14];
        let v: Vec<char> = group.chars().collect();
        let mut y = v.clone();

        y.sort();
        y.dedup();

        if v.len() == y.len() {
            println!("{}", i + 14);
            break;
        }

        i += 1;
    }
}
